mapper.update:
UPDATE tb_stocktaking
stock_num = #{stockNum}, stocktaking_num = #{stocktakingNum}, stocktaking_staff_id = #{stocktakingStaffId}, stocktaking_status = #{stocktakingStatus}, stocktaking_remarks = #{stocktakingRemarks}, stocktaking_profit_loss_status = #{stocktakingProfitLossStatus}, stocktaking_price = #{stocktakingPrice}
WHERE stocktaking_id = #{stocktakingId} AND stocktaking_stock_goods_id = #{stocktakingStockGoodsId}

mapper.select:
SELECT 
*
FROM
tb_stocktaking s


        AND
        s.stocktaking_id = #{stocktakingCondition.stocktakingId}


        AND
        s.stocktaking_stock_goods_id = #{stocktakingCondition.stocktakingStockGoodsId}


        AND
        s.stocktaking_status = #{stocktakingCondition.stocktakingStatus}


        AND
        s.stocktaking_profit_loss_status = #{stocktakingCondition.stocktakingProfitLossStatus}


ORDER BY stocktaking_time DESC
LIMIT #{rowIndex},#{pageSize}
